package com.jiyi.joke.bean;

public class QuesBean {
	private String ques_id;
	private String ques_content;
	private String ques_email;
	private String ques_time;
	
	public QuesBean() {
		// TODO Auto-generated constructor stub
	}

	public String getQues_id() {
		return ques_id;
	}

	public void setQues_id(String ques_id) {
		this.ques_id = ques_id;
	}

	public String getQues_content() {
		return ques_content;
	}

	public void setQues_content(String ques_content) {
		this.ques_content = ques_content;
	}

	public String getQues_email() {
		return ques_email;
	}

	public void setQues_email(String ques_email) {
		this.ques_email = ques_email;
	}

	public String getQues_time() {
		return ques_time;
	}

	public void setQues_time(String ques_time) {
		this.ques_time = ques_time;
	}
	

}
