package com.jiyi.joke.bean;
/**
 * 用户bean
 * @author JIYI
 *
 */
public class User {
	private String username;
	private String userpwd;
	private String qq_toke;
	private String xlweibo;
	private String weixin;
	private String sex;
	private String motto;
	private String id;
	
	public User() {
		// TODO Auto-generated constructor stub
	}

	
	
	public String getSex() {
		return sex;
	}



	public void setSex(String sex) {
		this.sex = sex;
	}



	public String getMotto() {
		return motto;
	}



	public void setMotto(String motto) {
		this.motto = motto;
	}



	public String getId() {
		return id;
	}



	public void setId(String id) {
		this.id = id;
	}



	public String getUsername() {
		return username;
	}

	public void setUsername(String username) {
		this.username = username;
	}

	public String getUserpwd() {
		return userpwd;
	}

	public void setUserpwd(String userpwd) {
		this.userpwd = userpwd;
	}

	public String getQq_toke() {
		return qq_toke;
	}

	public void setQq_toke(String qq_toke) {
		this.qq_toke = qq_toke;
	}

	public String getXlweibo() {
		return xlweibo;
	}

	public void setXlweibo(String xlweibo) {
		this.xlweibo = xlweibo;
	}

	public String getWeixin() {
		return weixin;
	}

	public void setWeixin(String weixin) {
		this.weixin = weixin;
	}
	

}
